document.addEventListener("DOMContentLoaded", function () {
  const titles = document.querySelectorAll(".megamenu-title");

  titles.forEach(title => {
    title.addEventListener("click", function (e) {
      e.preventDefault(); // Ngăn chặn chuyển hướng nếu <a>

      const menuItem = this.closest(".menu-item");
      const submenu = menuItem.querySelector(".dropdown-menus");

      if (submenu) {
        submenu.classList.toggle("menu_open");

        // Đóng các submenu khác nếu bạn muốn
        document.querySelectorAll(".dropdown-menus").forEach(menu => {
          if (menu !== submenu) {
            menu.classList.remove("menu_open");
          }
        });
      }
    });
  });
});
